                        axisBorder: {
                            show: false,
                        },
                    },
                };
            }
            if (document.getElementById("pie-chart") && typeof ApexCharts !== "undefined") {
                const chart = new ApexCharts(document.getElementById("pie-chart"), getChartOptions());
                chart.render();
            }
        });
    </script>';
    } else {
        echo "0 results";
    }
    ?>
</body>

